.ray-bourque .esg-filter-wrapper.eg-search-wrapper  .eg-search-clean.hovered,
.ray-bourque .esg-filter-wrapper.eg-search-wrapper  .eg-search-submit.hovered {
	color: #000;
	box-shadow: none;
	background: #fff;
}

.ray-bourque .esg-navigationbutton.hovered * {
	color: #fff;
}

.ray-bourque .esg-sortbutton-order.hovered .tp-desc {
	border-color: #fff;
	border-color: rgba(255, 255, 255, 0.2);
	color: #fff;
	box-shadow: 0px -3px 5px 0px rgba(0, 0, 0, 0.13) !important;
}

.ray-bourque .esg-filter-checked {
	color: rgba(255,255,255,.5);
  	border: 1px solid rgba(255,255,255,.5);
	background: transparent;
	font-size: 9px;
	font-weight: 300;
	vertical-align: middle;
  	line-height: 12px;
	margin-left: 7px;
  	display: inline-block;
  	width: 14px;
  	height: 14px;
  	transform: translateY(-2px);
}

.ray-bourque .esg-filter-wrapper.dropdownstyle .esg-filter-checked {
	margin-left: 0;
	margin-right: 10px;
	border:1px solid rgba(255,255,255,.2);
}

.ray-bourque .esg-filterbutton.selected .esg-filter-checked,
.ray-bourque .esg-filterbutton.hovered .esg-filter-checked {
	color: #000;
  	border: 1px solid #000;
}

/* */
.ray-bourque .esg-filter-wrapper.eg-search-wrapper {
	white-space: nowrap;
}


.ray-bourque .esg-filter-wrapper.eg-search-wrapper .eg-search-clean,
.ray-bourque .esg-filter-wrapper.eg-search-wrapper .eg-search-submit {
	width:40px;
	padding:0px;
	margin-left:5px;
	margin-right:0px;
}

.ray-bourque .esg-filter-wrapper.eg-search-wrapper .eg-search-input {
	width:auto;
}

.ray-bourque .esg-dropdown-wrapper {
	transform: translateZ(10px) translateX(-50%);
	left: 50%;
	background: #000;
	border-radius: 0;
  	padding: 0;
}

.ray-bourque .esg-dropdown-wrapper .esg-filterbutton {
	position: relative;
	border: none;
	box-shadow: none;
	text-align: left;
	color: #fff;
	background: transparent;
	line-height: 25px;
	min-height: 25px;
  	padding: 10px 20px;
  	display: inline-block;
  	width: 100%;
  	box-sizing: border-box;
  	margin: 0;
}

.ray-bourque .esg-dropdown-wrapper .esg-filterbutton.hovered,
.ray-bourque .esg-dropdown-wrapper .esg-filterbutton.selected {
	color: #000;
  	background: #fff;
}


.ray-bourque .esg-selected-filterbutton .eg-icon-down-open {
	margin-right: -10px;
	color:#fff;
	font-size:12px;
}

.ray-bourque .esg-sortbutton-order.eg-icon-down-open:before {
    content: "arrow_downward";
    font-family: "Material Icons";
  	font-size: 19px;
  	vertical-align: middle;
}

